<?php
namespace app\admin\controller;

use heephp\bulider\form;
use heephp\config;

class moban extends adminBase
{

    public function setting()
    {

        $fname = ROOT . '/skins/' . conf('website_skin') . '/page.json';
        if (!is_file($fname)) {
            $this->assign('msg', '当前模板不需要设置');
            return $this->fetch();
        }
        $sett = json_decode(file_get_contents($fname), true);
        $form = new form('save_setting');
        return $this->fetch();
    }

    public function online(){
        return $this->fetch();
    }

    public function manager()
    {

        $skins = [];
        $path = ROOT . '/skins/';
        foreach_dir($path, function ($val, $p) use (&$skins) {
            if (is_dir($p . $val)) {
                $sk['title'] = $val;

                $imgf = $p . $val . '/small.gif';
                if (file_exists($imgf))
                    $sk['img'] = 'data:image/gif;base64,' . imgToBase64($imgf);
                else
                    $sk['img'] = 'data:image/jpeg;base64,';

                $sk['checked'] = (conf('website_skin') == $val) ? 'checked' : '';
                $skins[] = $sk;
            }
        });

        $this->assign('skins', $skins);
        return $this->fetch();
    }

    public function save()
    {
        $moban = request('get.moban');
        if (conf('website_skin', $moban)) {
            cache()->clear();
            return $this->redirect('manager');
        } else
            return $this->error('模板' . $moban . '没有进行更改');
    }

    function ajax_template_dir()
    {
        $dir = urldecode(request('post.dir'));

        $root = ROOT . '/skins/' . conf('website_skin');
        $str = '';
        if (file_exists($root . $dir)) {
            $files = scandir($root . $dir);
            natcasesort($files);
            if (count($files) > 2) { /* The 2 accounts for . and .. */
                $str = "<ul class=\"jqueryFileTree\" style=\"display: none;\">";
                // All dirs
                foreach ($files as $file) {
                    if (file_exists($root . $dir . $file) && $file != '.' && $file != '..' && is_dir($root . $dir . $file)) {
                        $str .= "<li class=\"directory collapsed\"><a href=\"#\" rel=\"" . htmlentities($dir . $file) . "/\">" . htmlentities($file) . "</a></li>";
                    }
                }
                // All files
                foreach ($files as $file) {
                    if (file_exists($root . $dir . $file) && $file != '.' && $file != '..' && !is_dir($root . $dir . $file) && pathinfo($file, PATHINFO_EXTENSION) == 'php') {
                        $ext = preg_replace('/^.*\./', '', $file);
                        $str .= "<li class=\"file ext_$ext\"><a href=\"#\" rel=\"" . htmlentities($dir . $file) . "\">" . htmlentities($file) . "</a></li>";
                    }
                }
                $str .= "</ul>";
            }
        }
        return $str;
    }
}