DROP TABLE IF EXISTS `heecms_comment`;
CREATE TABLE `heecms_comment` (
  `comment_id` int(11) NOT NULL AUTO_INCREMENT,
  `category_id` int(11) DEFAULT NULL COMMENT '栏目的ID',
