#
# Structure for table "heecms_grabs"
#

DROP TABLE IF EXISTS `heecms_grabs`;
CREATE TABLE `heecms_grabs` (
  `grabs_id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `desc` varchar(255) DEFAULT NULL,
  `url` varchar(255) DEFAULT NULL COMMENT '网站网址',
  `users_id` varchar(255) DEFAULT NULL,
  `category_id` int(11) DEFAULT '0' COMMENT '需要插入的category',
  `epage` int(11) DEFAULT '1' COMMENT '每次采集的详细页数量',
  `urls` text COMMENT '要采集的网址',
  `startpage` int(11) DEFAULT NULL COMMENT '开始的页码',
  `endpage` int(11) DEFAULT NULL COMMENT '结束的页码',
  `linkstart` varchar(255) DEFAULT NULL,
  `linkend` varchar(255) DEFAULT NULL,
  `baseurl` varchar(255) DEFAULT NULL,
  `filterlinks` varchar(3000) DEFAULT NULL COMMENT '要过滤的网址',
  `titlestart` varchar(1000) DEFAULT NULL,
  `titleend` varchar(1000) DEFAULT NULL,
  `authorstart` varchar(1000) DEFAULT NULL,
  `authorend` varchar(1000) DEFAULT NULL,
  `timestart` varchar(1000) DEFAULT NULL,
  `timeend` varchar(1000) DEFAULT NULL,
  `contentstart` varchar(1000) DEFAULT NULL,
  `contentend` varchar(1000) DEFAULT NULL,
  `filterhtml` varchar(2000) DEFAULT NULL COMMENT '过滤内容html tags',
  `create_time` int(11) DEFAULT NULL,
  `update_time` int(11) DEFAULT NULL,
  `delete_time` int(11) DEFAULT NULL,
  PRIMARY KEY (`grabs_id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='抓取数据方案';

#
# Data for table "heecms_grabs"
#

/*!40000 ALTER TABLE `heecms_grabs` DISABLE KEYS */;
