DROP TABLE IF EXISTS `heecms_grabs_detail`;
CREATE TABLE `heecms_grabs_detail` (
  `grabs_detail_id` int(11) NOT NULL AUTO_INCREMENT,
  `grabs_id` int(11) DEFAULT NULL,
  `url` varchar(3000) DEFAULT NULL,
  `create_time` int(11) DEFAULT NULL COMMENT '采集时间',
  `users_id` int(11) DEFAULT NULL COMMENT '采集人',
  `title` varchar(1000) DEFAULT NULL,
  `author` varchar(1000) DEFAULT NULL,
  `time` varchar(255) DEFAULT NULL,
  `content` longtext,
  `firstpic` varchar(2000) DEFAULT NULL,
  PRIMARY KEY (`grabs_detail_id`)
) ENGINE=MyISAM AUTO_INCREMENT=421 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='抓取列表页网址';

